def generate_prompt():
    noun = random.choice(list(nouns))
    verb = random.choice(list(verbs))
    adjective = random.choice(list(adjectives))
    feature_keys = random.choices(list(feature_probabilities.keys()), weights=list(feature_probabilities.values()), k=1)[0]
    feature_strings = list(map(lambda x: features[x], feature_keys))
    prompt = f'Write a short story (3-5 paragraphs) which only uses very simple words that a 3 year old child would understand. The story should use the verb "{verb}", the noun "{noun}" and the adjective "{adjective}". '
    if len(feature_strings) > 0:
        prompt += f'The story has the following features: {", ".join(feature_strings)}. '
    prompt += 'Remember to only use simple words!'
    return prompt

def generate_prompts(n):
    return [generate_prompt() for i in range(n)]

print(generate_prompts(2))